export class CoatingAmount{constructor(a={}){this.filmDensity=11e5,this.weightGain=.03,this.tabletWeight=.4,this.tabletArea=.01,Object.getOwnPropertyNames(this).map(b=>{a[b]&&(this[b]=a[b])})}get weightGainPercent(){return(100*this.weightGain).toFixed(2)}get coatingWeight(){return this.tabletWeight*this.weightGain}set coatingWeight(a){this.weightGain=a/this.tabletWeight}get filmThickness(){return this.coatingCoverage/this.filmDensity}set filmThickness(a){this.coatingCoverage=a*this.filmDensity}get coatingCoverage(){return this.coatingWeight/this.tabletArea}set coatingCoverage(a){this.coatingWeight=a*this.tabletArea}toJSON(){return Object.assign({},this,{weightGainPercent:this.weightGainPercent,densityInGML:this.densityInGML,coatingWeight:this.coatingWeight,filmThickness:this.filmThickness,coatingCoverage:this.coatingCoverage})}}