export class CoatingAmount{constructor(a={}){this.filmDensity=11e5,this.weightGain=.03,this.tabletWeight=.4,this.tabletArea=.01,Object.getOwnPropertyNames(this).map(b=>{a[b]&&(this[b]=a[b])})}get coatingWeight(){return this.tabletWeight*this.weightGain}set coatingWeight(a){this.weightGain=a/this.tabletWeight}get filmThickness(){return this.coatingCoverage/this.filmDensity}set filmThickness(a){this.coatingCoverage=a*this.filmDensity}get coatingCoverage(){return this.coatingWeight/this.tabletArea}set coatingCoverage(a){this.coatingWeight=a*this.tabletArea}toJSON(){return Object.assign({},this,{coatingWeight:this.coatingWeight,filmThickness:this.filmThickness,coatingCoverage:this.coatingCoverage,formatted:{weightGain:`${(100*this.weightGain).toFixed(2)}%`,coatingWeight:`${(1e3*this.coatingWeight).toFixed(2)} mg`,filmThickness:`${(1e6*this.filmThickness).toFixed(2)} \u{000B5}m`,coatingCoverage:`${(1e3*this.coatingCoverage/1e4).toFixed(2)} mg/cm2`}})}}